#!/bin/bash

# Test 1: Long Currency Code
curl --location --globoff 'http://localhost:8080/exchange-rate/?currency1=%24(printf%20%27BTC%.0s%27%20{1..1000})&currency2=EUR'

# Test 2: Missing currency2
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC'

# Test 3: Missing currency1
curl --location 'http://localhost:8080/exchange-rate/?currency2=EUR'

# Test 4: Very long currency1
curl --location 'http://localhost:8080/exchange-rate/?'

# Test 5: Invalid currency2
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC&currency2=EUR%24'

# Test 6: Invalid characters in currency1
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC%24#&currency2=EUR'

# Test 7: Missing currency2 value
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC&currency2='

# Test 8: No query parameters
curl --location 'http://localhost:8080/exchange-rate/'

# Test 9: Valid currency1 and invalid currency2
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC&currency2=XYZ'

# Test 10: Invalid currency1 and valid currency2
curl --location 'http://localhost:8080/exchange-rate/?currency1=XYZ&currency2=EUR'

# Test 11: Valid request
curl --location 'http://localhost:8080/exchange-rate/?currency1=BTC&currency2=EUR'
